    @Override public boolean onOptionsItemSelected(MenuItem item) {
        int id = item.getItemId();
        if (id == R.id.action_statistics) {
            DialogStatistics dialogStatistics = new DialogStatistics();
            dialogStatistics.setCantidadFiguritas(cantidadFiguritas);
            dialogStatistics.show(getSupportFragmentManager(), "");
            return true;
        }
        if (id == R.id.acercaDe) {
            DialogInformation dialogInformation = new DialogInformation();
            dialogInformation.show(getSupportFragmentManager(), "");
            return true;
        }
        return super.onOptionsItemSelected(item);
    }

